  const [task,setTask]=useState([
    {title:"task-1"},
    {title:"task-2"},
    {title:"task-3"}
  ])
  return (
    <>
    <div className="todo-container">
       <h1><u>Todo List</u></h1>
       <div className='todo-add-task'>
       <Addtask/>    
       </div>
       <div className='todo-see-list'>
        task.map((value)=>{})
        <Listtask/>
       </div>
     
    </div>
    </>
  )
}
