import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

export default function () {
  const [value, setValue] = useState("");

  const check = (e) => {
    e.preventDefault(); // Prevent form submission
    if (value.trim() === '') {
      alert('Please enter a value before adding the task.');
      return;
    }
    console.log(value);
    // Do whatever you want to do with the value, like adding it to a list, etc.
  }

  return (
    <div className='add-task'>
      <form onSubmit={check}>
        <input
          type="text"
          id='t1'
          placeholder='Add A New Task'
          value={value}
          onChange={(e) => setValue(e.currentTarget.value)}
          required
        />
        <Button type="submit" variant="primary" id="add-task-btn">Add Task</Button>
      </form>
    </div>
  )
}
